OLED oled;

int counter;

void OLED::Init(void){
	MX_I2C2_Init();

	osDelay(200);
	counter = 0;

	OLED_reset();
	OLED_Init_Registers();
	//reset_buffer();
	setImage(1);
	update_all_screen();

}

void OLED::update_all_screen(){
	for(int i = 0; i < OLED_BUFFER_SIZE; i++) {
		send_DATA(buffer[i]);
	}
}

void OLED::setImage(unsigned int imageId){
	//uint8_t* imageArrayHandler;

	switch(imageId){
	case 1:
		//imageArrayHandler = this->logo_knr;
		break;
	}

	for(int i = 0; i < OLED_BUFFER_SIZE; i++) {
		//buffer[i] = imageArrayHandler[i];
		buffer[i] = logo_knr[i];
		//osDelay(5);
	}
}

void OLED::send_REG(uint8_t cmd){
	uint8_t W_Buf[2] ;
	W_Buf[0] = 0x00;
	W_Buf[1] = cmd;
	HAL_I2C_Master_Transmit(&hi2c2, (0X3D << 1) | 0X00, W_Buf, 2, 0x10);
	//osDelay(5);
}


void OLED::send_DATA(uint8_t data){
	uint8_t W_Buf[3] ;
	W_Buf[0] = 0x40;
	W_Buf[1] = data;
	HAL_I2C_Master_Transmit(&hi2c2, (0X3D << 1) | 0X00, W_Buf, 2, 0x10);
	osDelay(1);
}

